// Built-in types
let parser = clap::value_parser!(String);
assert_eq!(format!("{:?}", parser), "ValueParser::string");
let parser = clap::value_parser!(std::ffi::OsString);
assert_eq!(format!("{:?}", parser), "ValueParser::os_string");
